declare
    ucName VARCHAR2(30);
begin
    select INDEX_NAME into ucName from USER_IND_COLUMNS cols where cols.table_name='COMPANY_TYPE' and cols.COLUMN_NAME='CODE';
    execute immediate 'ALTER TABLE COMPANY_TYPE DROP CONSTRAINT ' || ucName;

    select INDEX_NAME into ucName from USER_IND_COLUMNS cols where cols.table_name='USAGE_TYPE' and cols.COLUMN_NAME='CODE';
    execute immediate 'ALTER TABLE USAGE_TYPE DROP CONSTRAINT ' || ucName;

    select INDEX_NAME into ucName from USER_IND_COLUMNS cols where cols.table_name='COMPANY_RELATION_TYPE' and cols.COLUMN_NAME='CODE';
    execute immediate 'ALTER TABLE COMPANY_RELATION_TYPE DROP CONSTRAINT ' || ucName;

    select INDEX_NAME into ucName from USER_IND_COLUMNS cols where cols.table_name='EMPLOYEE_CUSTOMER_RELATION' and cols.COLUMN_NAME='CODE';
    execute immediate 'ALTER TABLE EMPLOYEE_CUSTOMER_RELATION DROP CONSTRAINT ' || ucName;

    select INDEX_NAME into ucName from USER_IND_COLUMNS cols where cols.table_name='BUSINESS_SEGMENT_TYPE' and cols.COLUMN_NAME='CODE';
    execute immediate 'ALTER TABLE BUSINESS_SEGMENT_TYPE DROP CONSTRAINT ' || ucName;

    select INDEX_NAME into ucName from USER_IND_COLUMNS cols where cols.table_name='PHONE_TYPE' and cols.COLUMN_NAME='CODE';
    execute immediate 'ALTER TABLE PHONE_TYPE DROP CONSTRAINT ' || ucName;

    select INDEX_NAME into ucName from USER_IND_COLUMNS cols where cols.table_name='CONTACT_DEPARTMENT' and cols.COLUMN_NAME='CODE';
    execute immediate 'ALTER TABLE CONTACT_DEPARTMENT DROP CONSTRAINT ' || ucName;

    select INDEX_NAME into ucName from USER_IND_COLUMNS cols where cols.table_name='CONTACT_TITLE' and cols.COLUMN_NAME='CODE';
    execute immediate 'ALTER TABLE CONTACT_TITLE DROP CONSTRAINT ' || ucName;

    select INDEX_NAME into ucName from USER_IND_COLUMNS cols where cols.table_name='LOCATION_TYPE' and cols.COLUMN_NAME='CODE';
    execute immediate 'ALTER TABLE LOCATION_TYPE DROP CONSTRAINT ' || ucName;
end;


