INSERT INTO LHAUL_ROUTE_LEG_STOP_TEMP (ID, NAME, LAT, LNG, LOCATION_ID)
SELECT SEQ_LHAUL_ROUTE_LEG_STOP.NEXTVAL, NAME, NVL(LAT, 0), NVL(LNG, 0), ID FROM
(
SELECT DISTINCT L.ID, L.NAME, L.LAT, L.LNG FROM LHAUL_ROUTE_LEG LRL
INNER JOIN LOCATION L ON L.ID = LRL.FROM_LOCATION_ID OR L.ID = LRL.TO_LOCATION_ID
);

